import React, { useState, useEffect } from 'react';
import {
  Button,
  Container,
  Dialog,
  DialogActions,
  DialogContent,
  DialogContentText,
  DialogTitle,
  Fab,
} from '@mui/material';
import AddIcon from '@mui/icons-material/Add';
import CarList from './CarList';
import CarEdit from './CarEdit';

function CRUD() {
  const [cars, setCars] = useState([]); // Initialize with your cars data
  const [editDialogOpen, setEditDialogOpen] = useState(false);
  const [editCar, setEditCar] = useState(null);

  const handleEdit = (carId) => {
    const carToEdit = cars.find((car) => car.id === carId);
    setEditCar(carToEdit);
    setEditDialogOpen(true);
  };

  const handleDelete = (carId) => {
    const updatedCars = cars.filter((car) => car.id !== carId);
    setCars(updatedCars);
  };

  const handleAddNew = () => {
    setEditCar(null);
    setEditDialogOpen(true);
  };

  const handleEditDialogClose = () => {
    setEditDialogOpen(false);
  };

  const handleSaveCar = (editedCar) => {
    if (editedCar.id) {
      // Edit existing car
      const updatedCars = cars.map((car) =>
        car.id === editedCar.id ? editedCar : car
      );
      setCars(updatedCars);
    } else {
      // Add new car
      const newCar = { ...editedCar, id: Date.now() }; // Assign a temporary ID
      setCars([...cars, newCar]);
    }
    setEditDialogOpen(false);
  };

  return (
    <Container>
      <Button
        variant='outlined'
        color='primary'
        onClick={handleAddNew}
        startIcon={<AddIcon />}
      >
        Agregar Auto
      </Button>
      <CarList cars={cars} onEdit={handleEdit} onDelete={handleDelete} />
      <Dialog
        open={editDialogOpen}
        onClose={handleEditDialogClose}
        maxWidth='sm'
        fullWidth
      >
        <DialogTitle>{editCar ? 'Editar' : 'Agregar nuevo auto'}</DialogTitle>
        <DialogContent>
          <CarEdit
            car={editCar || {}}
            onSave={handleSaveCar}
            onCancel={handleEditDialogClose}
          />
        </DialogContent>
      </Dialog>
    </Container>
  );
}

export default CRUD;
