import React, { useState } from 'react';
import {
  Button,
  Card,
  CardActions,
  CardContent,
  TextField,
  Typography,
} from '@mui/material';

function CarEdit({ car, onSave, onCancel }) {
  const [editedCar, setEditedCar] = useState(car);
  const [selectedImage, setSelectedImage] = useState(null);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEditedCar({
      ...editedCar,
      [name]: value,
    });
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    setSelectedImage(file);
  };

  const handleSave = () => {
    // Create a FormData object to send image and other data to the backend
    const formData = new FormData();
    formData.append('image', selectedImage); // 'image' should match your backend's file field name
    formData.append('make', editedCar.make);
    formData.append('model', editedCar.model);
    formData.append('year', editedCar.year);
    formData.append('price', editedCar.price);

    onSave(formData);
  };

  return (
    <Card>
      <CardContent>
        <Typography variant='h6' gutterBottom>
          Editar
        </Typography>
        <input type='file' accept='image/*' onChange={handleImageChange} />
        <TextField
          name='make'
          label='Fabricante'
          value={editedCar.make}
          onChange={handleInputChange}
          fullWidth
        />
        <TextField
          name='model'
          label='Modelo'
          value={editedCar.model}
          onChange={handleInputChange}
          fullWidth
        />
        <TextField
          name='year'
          label='AÃ±o'
          value={editedCar.year}
          onChange={handleInputChange}
          fullWidth
        />
        <TextField
          name='price'
          label='Precio'
          value={editedCar.price}
          onChange={handleInputChange}
          fullWidth
        />
      </CardContent>
      <CardActions>
        <Button color='primary' onClick={handleSave}>
          Guardar
        </Button>
        <Button color='secondary' onClick={onCancel}>
          Cancelar
        </Button>
      </CardActions>
    </Card>
  );
}

export default CarEdit;
