import React, { useState } from 'react';
import {
  Button,
  Card,
  CardActions,
  CardContent,
  TextField,
  Typography,
} from '@mui/material';

function CarEdit({ car, onSave, onCancel }) {
  const [editedCar, setEditedCar] = useState(car);

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setEditedCar({
      ...editedCar,
      [name]: value,
    });
  };

  const handleSave = () => {
    onSave(editedCar);
  };

  return (
    <Card>
      <CardContent>
        <Typography variant='h6' gutterBottom>
          Edit Car
        </Typography>
        <TextField
          name='make'
          label='Make'
          value={editedCar.make}
          onChange={handleInputChange}
          fullWidth
        />
        <TextField
          name='model'
          label='Model'
          value={editedCar.model}
          onChange={handleInputChange}
          fullWidth
        />
        <TextField
          name='year'
          label='Year'
          value={editedCar.year}
          onChange={handleInputChange}
          fullWidth
        />
        <TextField
          name='price'
          label='Price'
          value={editedCar.price}
          onChange={handleInputChange}
          fullWidth
        />
      </CardContent>
      <CardActions>
        <Button color='primary' onClick={handleSave}>
          Save
        </Button>
        <Button color='secondary' onClick={onCancel}>
          Cancel
        </Button>
      </CardActions>
    </Card>
  );
}

export default CarEdit;
